def find_two_sum_indices(nums, target):
    num_to_index = {}
    for index, num in enumerate(nums):
        complement = target - num
        if complement in num_to_index:
            first_index = num_to_index[complement]
            second_index = index
            return sorted([first_index, second_index])
        num_to_index[num] = index

if __name__ == "__main__":
    try:
        n = int(input())
        nums_list = list(map(int, input().split()))
        target_value = int(input())
        result_indices = find_two_sum_indices(nums_list, target_value)
        if result_indices:
            print(result_indices[0], result_indices[1])
    except (ValueError, IndexError) as e:
        print(f"An error occurred with the input: {e}")
