import java.util.Scanner;

class Product {
    private int id;
    private int nameCode;
    private double price;

    public Product(int id, int nameCode, double price) {
        this.id = id;
        this.nameCode = nameCode;
        this.price = price;
    }

    public void displayProductDetails() {
        System.out.println("Product ID: " + this.id);
        System.out.println("Product Name Code: " + this.nameCode);
        System.out.printf("Product Price: â‚¹%.1f\n", this.price);
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int productId = scanner.nextInt();
        int productNameCode = scanner.nextInt();
        double productPrice = scanner.nextDouble();

        Product product = new Product(productId, productNameCode, productPrice);

        product.displayProductDetails();

        scanner.close();
    }
}
